INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10001418', '冰菓 氷菓', '8.7', '2012', '[''武本康弘'']', '[''米泽穗信'', ''贺东招二'']', '[''中村悠一'', ''佐藤聪美'', ''阪口大助'', ''茅野爱衣'', ''雪野五月'', ''置鲇龙太郎'', ''小山茉美'', ''早见沙织'', ''由加奈'', ''悠木碧'', ''川原庆久'', ''浅野真澄'', ''丰崎爱生'', ''小仓唯'', ''阿部敦'', ''伊濑茉莉也'', ''入野自由'', ''小清水亚美'', ''小西克幸'', ''日笠阳子'']', '[''动画'', ''悬疑'']', ' www.kyotoanimation.co.jp/kotenbu/', '[''日本'']', '', '[''日语'']', '', '[''冰果'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1353973170.jpg', '本片是以神木高中“古典部”为舞台，围绕四个部员折木奉太郎、千反田爱瑠、福部里志、伊原摩耶花展开的校园推理剧。神木高中一年级生折木奉太郎是一个“节能”主义者，他信奉“不必要的事不做，非做不可的事就尽快解决”，过着“灰色”的高中生活。不参加任何社团的他，却因为姐姐的拜托而加入了濒临废部的“古典部”。在古典部他结识了好奇心旺盛的千金小姐千反田爱瑠，国中好友福部里志、伊原摩耶花也相继加入古典部。原来千反田失踪已久的舅舅就是当年古典部的部长，于是四人开始着手调查四十五年前古典部的神秘事件，而最大的线索就是当年的社刊“冰果”。冰果里隐藏了什么样的秘密，当年的部长为什么要退学……本动画改编自日本推理作家米泽穗信的古典部系列小说。', '[]', '[''3893384'', ''26169716'', ''6852358'', ''5349275'', ''19967854'', ''11226092'', ''5416070'', ''25955381'', ''6085356'', ''20495792'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10001427', '秦淮八艳', '', '1991', '[''戴家乐'']', '[''周维先'', ''陆拂明'']', '[''傅艺伟'', ''巫刚'', ''陈国典'', ''韩剑嵘'', ''吴涛'', ''乔立生'', ''刘鸿声'', ''李炳军'', ''李洁'', ''梅婷'']', '[''爱情'', ''纪录片'', ''传记'', ''历史'', ''古装'']', '', '[''中国大陆'']', '', '[''汉语普通话'']', '', '[''秦淮八艳之陈圆圆，卞玉京'']', 'https://img3.doubanio.com/lpic/s10279585.jpg', '十里秦淮，这红极一时的仙都乐土，是胭脂染红的，美酒映红的，鲜血染红的。河旁妓家临次，水中火龙灯船。多少宗室王孙、公侯名士在这里传歌唤月，醉卧红袖，或慷慨陈词，结党明志。清代一个画家画出了最出名的八个青楼女子，于是有了“秦淮八艳”之称。马湘兰生于一五四八年，她相貌虽不出众，但秉性灵秀，能诗擅画，是明代著名的女诗人，女画家。有关她的许多故事一直流传至今。寇白门，就是以其娟娟静美，跌宕风流，豪爽兼古侠士风为世人称道，列为“八艳”之一。陈圆圆是明末清初的一代名妓，她天生丽质，才艺双绝，有关吴三桂为她引清兵入关的传说，一直流传至今。卞玉京，又名卞赛，出生宦门，因父早亡，姐妹二人沦为歌伎。卞玉京虽才华出众，但终不得意，后在苏州皈依道观。', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10001432', '颠倒的帕特玛 サカサマのパテマ', '7.9', '2013', '[''吉浦康裕'']', '[''吉浦康裕'']', '[''藤井雪代'', ''冈本信彦'', ''大畑伸太郎'', ''福松进纱'', ''加藤将之'']', '[''动画'']', ' patema.jp/', '[''日本'']', '[''2013-06-13(法国安纳西动画电影节)'', ''2013-11-09(日本)'']', '[''日语'']', '99', '[''颠倒的帕蒂玛(港)'', ''帕蒂玛的颠倒世界'', ''倒转的帕提玛'', ''倒置的Patma'', ''相反的Patma'', ''SakasamanoPatema'', ''PatemaInverted'']', 'https://img1.doubanio.com/view/movie_poster_cover/lpst/public/p2043944718.jpg', '在秩序井然的艾嘉王国，人类谨遵祖先的教训，绝不抬头仰望苍穹，放纵思绪流淌狂奔。传说在很久以前，地上的人们挑战造物，结果致令重力反转，难以数计的人类和建筑“坠入”蓝天，销声匿迹。但森严的秩序和自由思想势必发生冲突，少年艾吉（冈本信彦配音）夜晚偷偷溜到野外，躺在草地上仰望繁星点点的夜空，而就在此时，身后的洞口出现了一个神色慌张的女孩。她叫帕特玛（藤井雪代配音），拥有和艾吉全然相反的重力场。为了防止帕特玛跌落到空中，艾吉小心翼翼将其带到小屋保护起来。可是政府很快发现了帕特玛的踪迹，最高领导人动员全部力量展开围捕。在你来我往的追逐过程中，艾嘉王国和反转人的秘密也悄然揭开……', '', '[''4160477'', ''24706479'', ''10558440'', ''10355625'', ''3908423'', ''1310177'', ''11499092'', ''20266597'', ''3861833'', ''1865703'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10001437', '大熊猫51的故事 51（ウーイー） 世界で一番小さく生まれたパンダ', '8.4', '2012', '[''塩浜雅之'']', '', '[''長谷川潤(配音)'']', '[''纪录片'']', ' http://www.panda51.jp/', '[''日本'']', '[''2012-02-11'']', '[''日语'']', '80', '', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1353745592.jpg', '2006年、中国・成都のパンダ研究基地に誕生した体重わずか51グラムのパンダの赤ちゃん「ウーイー」の成長を追ったドキュメンタリー。通常の3分の1しか体重のない、史上最も小さなパンダの赤ちゃんとして生まれたウーイー。その命をつなぐべく奔走する飼育員たちの姿や、ウーイーの成長記録とともに、想像妊娠や代理母、育児拒否といった知られざるパンダの子育ての実態や生態にもスポットライトを当てる。', '', '[''3732734'', ''3003828'', ''4133149'', ''4735152'', ''11615881'', ''1417430'', ''26301568'', ''10833925'', ''10508914'', ''24751775'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10001455', '武之舞', '9.9', '1995', '', '[''贾磊磊'']', '', '[''纪录片'']', '', '[''中国大陆'']', '[''1997年2月'']', '[''汉语普通话'']', '22', '[''武之舞——中国武侠电影的形态与神魂'']', 'https://img3.doubanio.com/lpic/s7028881.jpg', '1995年中国电影学会摄制的《武之舞》22集大型纪录片，对当时中国武侠电影文化进行了系统的概括和评述，非常具有收藏性。当时全国数千家电视台反复轮番播放，但鉴于当时的网络传媒不发达，没有人能共享出来，现在想求之而不得。以下为纪录片中著名解说片段：古代侠士，多为善武重义搏命者，浮生于乱世，名噪于道衰；或立捐躯忘亲之志，或结生死与共之情，故以信义显名于天下，以勇武取重于诸侯。遥望古时，先有《汉书》、《史记》为侠列传，后有唐诗百首颂其英姿。时至近代，国人承传侠义小说之正脉，创作出自立于世界影坛的中国武侠电影。其间固有妖孽滋生，神怪泛滥，然而后世来者竟侠电影中发扬华夏志士崇侠尚义之武风，挥写民族英豪彪炳千秋之正气，使中国武侠电影终成世界银海一道璀璨风景！', '', '[''1302738'', ''1307605'', ''1307115'', ''1467585'', ''1301180'', ''1299085'', ''1303587'', ''1300551'', ''1793883'', ''1298556'']');
