INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10080051', '真实魔鬼游戏3 リアル鬼ごっこ3', '3.8', '2012', '[''安里麻里'']', '[''安里麻里'']', '[''山崎贤人'', ''岸田タツヤ'', ''山谷花纯'', ''森田直幸'']', '[''惊悚'']', '', '[''日本'']', '[''2012-05-12'']', '[''日语'']', '95', '[''夺命捉迷藏3'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1539683752.jpg', '在一个远离大都会的小镇，高中生卓（山崎贤人饰）过着百无聊赖的生活。某天，他和好友儿玉（森田直幸饰）翘客躺在学校房顶打发时间，就在此时传来了国王的最新政令。电视屏幕中，戴着白色假发的怪怪国王向全校学生宣布开展“真实魔鬼游戏”。经过儿戏般的选择，所有B型血的人成为被抹杀的目标。话音刚落，学校被一群神秘之人封闭，一群穿着黑衣戴着面具的恶鬼冲入学校，开始针对B型血学生长达一个小时的杀戮行动。阿卓和曾经的好友王子（岸田タツヤ饰）以及低年级女生莉乃（山谷花纯饰）必须想尽办法从这个隔绝的地狱中逃脱……本片根据山田悠介的同名原作改编。', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10174444', '剧场版魔法少女小圆 前篇 起始的故事 劇場版 魔法少女まどか☆マギカ [前編] 始まりの物語', '8.6', '2012', '[''新房昭之'']', '[''虚渊玄'']', '[''悠木碧'', ''斋藤千和'', ''水桥香织'', ''喜多村英梨'', ''加藤英美里'', ''野中蓝'']', '[''动画'']', '', '[''日本'']', '[''2012-10-06(日本)'']', '[''日语'']', '131', '[''PuellaMagiMadokaMagicatheMovie'']', 'https://img1.doubanio.com/view/movie_poster_cover/lpst/public/p1634119717.jpg', '市立见泷原中学，又迎来平静祥和的一天。就读于二年级的女孩鹿目圆（悠木碧配音）和朋友美树沙耶加（喜多村英梨配音）、志筑仁美（新谷良子配音）尽情讨论恋爱的话题，享受少女的快乐青春。这时班上转来了名叫晓美焰（斋藤千和配音）的新生，这个带着些许神秘气质的女孩让小圆颇感疑惑。与沙耶加下学回家路上，小圆听到呼救声，声音来自一只浑身伤痕累累的未知生物，而追杀它的正是晓美焰。危急时刻，她们得到同为见泷原中学学生的魔法少女巴麻美（水桥香织配音）的帮助，进而得知那只名叫丘比的生物有能够帮助人变成魔法少女的能力。少女们无意间开启了通往另一个世界的大门……本片为魔法少女的剧场版，分三部上映，由TV版12话重新编集而成。', '', '[''10569150'', ''24861238'', ''10428479'', ''21351041'', ''4074292'', ''25772498'', ''25957232'', ''25779156'', ''5403927'', ''10453725'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10149501', '自杀者光顾的面包店 カミハテ商店', '', '2012', '[''山本起也'']', '[''山本起也'']', '[''高橋惠子'', ''寺島進'']', '[''剧情'']', '', '[''日本'']', '[''2012-11-10'']', '[''日语'']', '104', '', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1666659764.jpg', '京都造形芸術大学の学生とプロのスタッフ・キャストがタッグを組む北白川派映画の第3弾作品で、自殺の名所となった崖の近くで商店を営む初老女性の目を通し、死生観への真意をなげかける人間ドラマ。山陰の小さな港町・上終（カミハテ）で、母の残した店を引き継ぎ、パンを焼いては細々とそれを売って生活している初老の女性・千代。いつの頃からか、店のそばにある断崖絶壁が自殺の名所となり、店に見知らぬ訪問客が現れるようになる。彼らは人生の最後に千代が焼いたパンを食べ、千代も彼らが自殺するであろうと気づいても止めることはせず、ただ見送った人々が崖に残していった靴を持ちかえる日々を送っていた。千代役は「花物語」（1986）以来23年ぶりの映画主演となる高橋惠子。千代の弟役に寺島進。', '', '[]');
