INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10439282', '北京房事', '7.6', '2011', '[''魏婷婷'']', '[''魏婷婷'', ''李雨'']', '[''郭朕宇'']', '[''动画'', ''短片'']', '', '[''中国大陆'']', '[''2011-06-22(中国大陆)'']', '[''汉语普通话'']', '5', '[''北京@房事'']', 'https://img3.doubanio.com/lpic/s7043055.jpg', '进入21世纪，房子成了国人衣食住行的头等大事。尤其作为祖国的心脏北京市，房子更成为让人操心的重中之重。按照数据统计，北京一个三口之家的平均居住面积应为45平方米的房子，但是国人买房子包含了公摊面积，所以说平均下来的面积就更小了。许多北漂在北京只能租房，打出隔断的小房子成为蚁族刻骨铭心的记忆。而随着相关禁令的出台以及针对非北京户口人群的相关限定，收入可怜的北漂面对飞速上涨的房价也只能望洋兴叹。34.9%的人属于房奴，收入的40%需要去还房贷。平凡的人，在偌大的城市里，你的房子在哪里呢？', '', '[''5216006'', ''11603393'', ''6129643'', ''24843417'', ''6003076'', ''20502708'', ''2139551'', ''6872262'', ''6730007'', ''4307623'']');
