INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10455635', '解脱了的光子 むきだしの光子', '7.5', '2006', '[''谷口祟'']', '[''谷口祟'']', '[''谷口祟'']', '[''动画'', ''短片'']', ' http://mc.adkda.net/index.htm', '[''日本'']', '', '[''日语'']', '6', '', 'https://img3.doubanio.com/lpic/s8978720.jpg', '光子（谷口崇配音）是一个普通的家庭主妇，她那烦人的婆婆利用一切机会抱怨光子作为一个儿媳妇的不负责任，苦闷的光子甚至希望能借助毒品来摆脱这个现实社会。就在这时，一个长着双腿和翅膀的肉球出现了，肉球自称“来自自由国度的使者”，他把光子传送到了一片绝对自由领域中，光子在那里度过了愉快的9天。第9天，自由使者告诉光子，如果她在这里的时间超过10天，那她就永远无法回到那烦人的世界中去了，是去是留，这一切都要靠光子自己来决定。光子会做出怎样的选择呢？短篇由著名的谷口崇独立制作，有着一如既往的脱力气氛和恐怖画风。在结尾处，众人拍手的场面实际上恶搞了动画《EVA》的结尾，这也是谷口崇的一贯作风。', '', '[''10455636'', ''4907953'', ''4907954'', ''4901506'', ''4907952'', ''25830807'', ''2281021'', ''5339124'', ''26348982'', ''5162264'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10455636', '森林的安藤 森の安藤', '7.9', '2006', '[''谷口祟'']', '[''谷口祟'']', '[''谷口祟'']', '[''动画'', ''短片'']', ' http://mc.adkda.net/index.htm', '[''日本'']', '', '[''日语'']', '5', '', 'https://img1.doubanio.com/lpic/s8978759.jpg', '在森林的最中央，一只长得很有喜感的熊向蜜蜂提出请求，那就是能否舔舔他。蜜蜂不太高兴，因为一天前狗熊也舔了他，可是舔起来没完，让蜜蜂君很是困扰。蜜蜂的言论激怒了狗熊，狗熊愤怒伸出手掌拍蜜蜂。这时候，自称安藤的企鹅出现了，他制止两个家伙的战斗，谁知狗熊却突然问起他的身世，安藤先生是不是人类。这个问题戳中了安藤的痛处，他张皇失措，假装接电话偷偷溜掉。在此之后，狗熊和蜜蜂前去请教德高望重的龟伯伯。当大家正讨论之际，房屋突然山摇地动，原来一辆挖掘机正在作业，试图拆掉他们的房子。危机时刻，安藤先生再度出现……', '', '[''4907953'', ''10455635'', ''4907954'', ''4901506'', ''4907952'', ''25830807'', ''6799194'', ''5345365'', ''4116987'', ''2281019'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10455645', '麻辣女兵', '5.1', '2012', '[''谷锦云'']', '[''田良'', ''周海丹'']', '[''王洋'', ''侯勇'', ''李晨'', ''杨烁'', ''马丽'', ''尹铸胜'', ''王煜'', ''刘潺'']', '[''剧情'', ''战争'']', '', '[''中国大陆'']', '', '[''汉语普通话'']', '', '', 'https://img1.doubanio.com/view/movie_poster_cover/lpst/public/p1671434898.jpg', '90后女孩假小子汤小米（王洋饰）性格叛逆，身为副旅长的母亲米蓝（马丽饰）把她送到军营，无奈汤小米桀傲不驯，整天闹着逃跑，很令米蓝头疼。米蓝与丈夫（尹铸胜饰）早年离异，而当老板的丈夫只知赚钱放弃了对女儿的管教，致使小米成了一个问题少女。在新兵连，米蓝对教官下了死命令，一定要剃好小米这颗“刺儿头”，教官唯命是从。经过一段时间的严酷训练，小米慢慢从中悟到了妈妈的良苦用心，当兵的经历使她重新认识了人生，更主要的是重新认识了妈妈米蓝，知道她当年为什么一头扎进部队义无反顾。历练之后的小米明白了：成长就是受伤、失去与理解的过程......', '[]', '[''10538050'', ''6989755'', ''26299388'', ''6808697'', ''4073995'', ''25899391'', ''10727048'', ''3137846'', ''3204595'', ''11615885'']');
