INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461354', '哆啦美与迷你哆啦SOS ドラミちゃん ミニドラSOS!!!', '7.4', '1989', '[''森协真琴'']', '', '[''横泽启子'', ''小原乃梨子'', ''北川智絵'']', '[''动画'']', '', '[''日本'']', '[''1989-03-11(日本)'']', '[''日语'']', '40', '', 'https://img3.doubanio.com/lpic/s10428815.jpg', '2011年的未来世界，大雄和静香如愿结为夫妻，并共同养育了可爱而调皮的儿子野比助。小助与爸爸截然相反，不仅体育方面十分擅长，而且经常欺负胖虎和小夫的儿子，真可谓风水轮流转。这一天，他回家后发现房间里有一个奇怪的球体，于是顺手将其送给胖虎的儿子小技。不久，哆啦美来到家中，她正是要寻找那个原本要寄送到1989年却因为自己潦草被错误投递到25年后的球体包裹。出于好奇，小技打开球体，发现里面躺着一个与哆啦A梦极为相似的红色小机器人，那正是来自未来的迷你哆啦。从未见过哆啦A梦的小助、小技和小树与迷你哆啦快乐地玩耍，不知不觉忘记了时间。消失的迷你哆啦和调皮的三个孩子，这可急坏了哆啦美……', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461355', '哆啦A梦族：怪盗哆啦邦谜样的挑战书 ザ☆ドラえもんズ 怪盗ドラパン謎の挑戦状！', '8.0', '1997', '[''米谷良知'']', '', '[''佐藤正治'', ''铃木みえ'', ''难波圭一'', ''林原惠'', ''神谷明'']', '[''动画'']', '', '[''日本'']', '[''1997-03-08(日本)'']', '[''日语'']', '30', '[''哆啦A梦七小子与怪盗哆啦邦'']', 'https://img1.doubanio.com/lpic/s8979009.jpg', '哆啦A梦七小子受到了寺尾台校长寄来的立体邮件，说是引起世间骚动的怪盗哆啦邦，顶上了校长的新研究所。哆啦A梦七小子匆忙赶了去，可等待他们的却是伪装成校长的哆啦邦。他把哆啦A梦等人变成了铜像，还夺走了好友通讯卡。可恰巧是，哆啦梅度三世跟哆啦利钮因为拿错了卡，幸运地没有被变成铜像。后来二人了解到，哆啦邦是因为阿奇莫夫博士绑架了少女绵绵，并用特殊的、能勒紧项圈要挟哆啦邦这么做。阿奇莫夫博士的目的实际上是为了给他的能控制世界所有机器人的阿奇莫夫机能量。最后，由于哆啦邦意识到被骗了，将七小子恢复了原样，并合力打败了阿奇莫夫。', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461356', '大雄的怀念奶奶 おばあちゃんの思い出', '9.4', '2000', '', '[''藤子·F·不二雄'']', '[''大山信代'', ''小原乃梨子'', ''立壁和也'', ''肝付兼太'', ''野村道子'', ''高村章子'', ''千千松幸子'', ''大本真基子'', ''关智一'', ''くじら'', ''佐久间玲'']', '[''动画'']', '', '[''日本'']', '[''2000-03-11'']', '[''日语'']', '26', '[''奶奶的思念'', ''奶奶的回忆'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2369337420.jpg', '《大雄的怀念奶奶》（おばあちゃんの思い出），是一部哆啦A梦电影作品系列的附篇作品之一，首映于2000年3月11日，是哆啦A梦短篇电影系列第三部，改编自1979年哆啦A梦电视动画《老祖母的愉快回忆》和1986年的哆啦A梦系列电视动画《我爱奶奶》。该电影跟《大雄的太阳王传说》同年上映。《大雄的怀念奶奶》也是2000年第55届日本每日电影奖获奖的动画电影。', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461357', '哆啦美与哆啦A梦族：宇宙乐园之千钧一发！ ドラミ&ドラえもんズ 宇宙ランド危機イッパツ!', '7.6', '2001', '[''錦織博'']', '[''池田眞美子'']', '[''よこざわけい子'', ''一龍斉貞友'', ''難波圭一'']', '[''动画'']', '', '[''日本'']', '[''2001-03-10'']', '[''日语'']', '15', '', 'https://img5.doubanio.com/lpic/s24524406.jpg', '全新的高科技游乐场SpaceLand在外太空正式开张，正好适逢猫型机器人同窗会，一向吵闹喧哗的哆拉A梦小子们也跑来凑热闹。偏偏哆拉A梦前一天吃坏了肚子，于是只得由哆拉美（よこざわけい子饰）带着这群小家伙们前往SpaceLand。不过，游乐场的情况有些奇怪，这里既看不到客人，工作人员也全不见踪影。糟糕的是，哆拉美他们还被卷入各种疯狂的设施之中不得抽身。似乎有某些坏蛋占领了这座乐园，哆拉美他们最终能否摆脱危机？拯救乐园呢？本片为2011年哆拉A梦剧场版《大雄与翼之勇者》附赠短片。', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461358', '我出生的那一天 ぼくの生まれた日', '8.5', '2002', '[''渡辺歩'']', '[''藤本信行'']', '[''大山羡代'', ''小原乃梨子'', ''野村道子'']', '[''动画'']', '', '[''日本'']', '[''2002-03-09(日本)'']', '[''日语'']', '28', '[''Bokunoumaretahi'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2369337625.jpg', '炎炎夏日，大雄（小原乃梨子配音）一蹦一跳行走在小路上。今天是8月7日，大雄的生日，他满心期待在这一天能得到所有人的称赞以及生日礼物。可是事与愿违，大雄遭到了爸爸妈妈的一通臭骂。此时此刻，他的心情就如同窗外的雷雨天，简直糟透了。回到房间后，伤心至极的大雄收拾行囊，离家出走，但是走遍城市每一个角落都没有他可以容身之处。正在这时，哆拉A梦（大山のぶ代配音）找到大雄，为了向大雄证明他确实是野比家的孩子，哆拉A梦带着好朋友乘坐时光机，来到了大雄出生的那一天。亲眼见证着自己的出生以及给予了深深厚望的父母，大雄的心中若有所悟……本片为2002年哆拉A梦剧场版《大雄与机器人王国》附赠短片。', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461364', '那夜的武士 その夜の侍', '6.8', '2012', '[''赤堀雅秋'']', '[''赤堀雅秋'']', '[''堺雅人'', ''山田孝之'', ''坂井真纪'', ''新井浩文'', ''绫野刚'', ''谷村美月'', ''安藤樱'', ''田口智朗'']', '[''剧情'']', ' sonoyorunosamurai.com', '[''日本'']', '[''2012-11-17(日本)'']', '[''日语'']', '119', '[''TheSamuraiThatNight'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1743793342.jpg', '自从五年前妻子（坂井真纪饰）死于交通肇事逃逸事故后，某小型铁厂厂长中村健一（堺雅人饰）一直沉浸在丧妻之痛中无法自拔。在过去的岁月里，妻子最后的电话留言和生前的遗物引起他无限的追思，也阻断这名颓败悲伤的男人向新生活迈进。与之相对，当年的肇事者木岛宏（山田孝之饰）完成两年刑期后出狱，却似乎没有任何反省的意识。他粗暴殴打疑似宣传其过去的同僚星叔（田口智朗饰），强迫好友小林（绫野刚饰）和他干着各种恶德之事。直到某天，中岛开始一封封收到刺杀他的通告，对方宣称将与之同归于尽。狂风暴雨之夜，两个男人的决断在所难免……本片由著名剧作家赤堀雅秋根据自己的同名戏剧改编。', '[''第56届伦敦电影节'', ''最佳导演处女作(提名)'', ''赤堀雅秋'']', '[''5273378'', ''22293087'', ''6959207'', ''3626439'', ''2278781'', ''10439244'', ''25866282'', ''2264227'', ''6844875'', ''21263717'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461365', '国家地理 佛罗伦斯黑暗秘辛 National Geographic Channel: Florence unlocked', '7.6', '2010', '', '', '', '[''纪录片'']', '', '[''美国'']', '', '[''英语'']', '47', '', 'https://img3.doubanio.com/lpic/s7056015.jpg', '意大利佛罗伦斯的地底下埋藏着一些秘密。这座城市曾一度是罗马军队驻扎地，后来发展成意大利文艺复兴的发源地。然而这番转变却让艺术家与其赞助者都付出代价。受启蒙而产生创新观念的人遭到监禁，建筑师要隐藏设计图以免被窃。皇室赞助者利用隐藏阶梯与秘道保持隐私，并监视政府与家人。如今，现代改革者运用最新科学解开古老的百年秘密，国家地理频道也趁机揭开了文艺复兴时期佛罗伦斯的黑暗面。', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461368', '犀利人妻 犀利人妻最終回：幸福男·不難', '5.5', '2012', '[''王珮华'', ''王仁里'']', '[''杨海薇'', ''王佩华'', ''陈慧如'', ''洪立妍'']', '[''隋棠'', ''温升豪'', ''王宥胜'', ''小小彬'', ''蔡淑臻'', ''朱芯仪'', ''马念先'', ''唐立淇'']', '[''爱情'']', '', '[''台湾'']', '[''2012-12-31(中国大陆)'', ''2012-08-17(台湾)'']', '[''汉语普通话'']', '109', '[''犀利人妻电影版'', ''TheFierceWifeFinalEpisode'']', 'https://img1.doubanio.com/view/movie_poster_cover/lpst/public/p1625336618.jpg', '谢安真（隋棠饰），离婚四年，单身四年，身兼职业OL与家庭主妇两种身分的她，将生活中的一切都打理得井然有序。女儿眼中的好妈妈、同事眼中的好上司、朋友眼中的好姐妹，但，她又会是谁的“好情人”呢？虽然对爱情仍然有些渴望，安真总用自己的生活很充实、很满足、很幸福来当藉口，因此，她与天蔚（宥胜饰）还是一直维持着“好朋友”的关系。但，连安真自己也没发现，她只是没有勇气，没有勇气再次投入一段“甜蜜却随时可能破碎的爱情”。在安真苦恼于是否该接受天蔚的感情时，前夫瑞凡（温升豪饰）却在此时出现，再次对她展开追求。在“亲情”与“爱情”、“安心”与“开心”、“习惯”与“改变”之间，安真是否能够把握当下的幸福，勇敢走向能让她发自内心微笑的那个人呢？幸福，真有么困难吗？', '', '[''10736586'', ''6436763'', ''11604102'', ''5446226'', ''11639159'', ''10554941'', ''10456963'', ''10567087'', ''10813688'', ''6062731'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461376', '黑板 第1夜 ブラックボード 第1夜', '7.7', '2012', '[''平野俊一'']', '[''井上由美子'']', '[''樱井翔'', ''宫泽理惠'', ''今井悠贵'', ''大岛优子'', ''中村苍'', ''菅田将晖'', ''森迫永依'', ''太贺'', ''铃木福'', ''小林星兰'', ''渡边一计'', ''惠俊彰'', ''安藤樱'', ''名取裕子'', ''北大路欣也'']', '[''剧情'']', ' www.tbs.co.jp/blackboard/', '[''日本'']', '[''2012-04-05'']', '[''日语'']', '116', '[''ブラックボード～時代と戦った教師たち～3夜連続放送SP第1夜'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2339240482.jpg', '1944年，第二次大战接近尾声，轴心国败局已定，世界将迎来和平的曙光。但在日本国内，依然有许多热血爱国的青年前赴后继奔向永远没有回头路的战场。某学校青年教师白滨正平（樱井翔饰）为光荣入伍而感到自豪，他在出征前的最后一堂课上鼓动学生们奔赴战场，为国捐躯，狂热的宣言和军歌由此改变了许多人的命运。二战结束后，在南洋丢掉一条胳膊的正平返回祖国，眼前尽是一片寥落破败的景象，百姓生活艰苦，为了生计无所不做，而曾经的敌人美国人则以拯救者的姿态出现在本土。他受邀在当年的学校任教，可是时代与思潮的沧桑巨变让正平经历了最为痛苦的灵魂风暴……本片为连续三夜的SPブラックボード第一部。', '', '[''10461377'', ''20020915'', ''10759862'', ''4139833'', ''6783744'', ''6973370'', ''2993061'', ''6807440'', ''10462924'', ''1887837'']');
