INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10461607', '食材花园 The Edible Garden', '9.2', '2010', '[''JulietGlaves'']', '[''AlysFowler'']', '[''AlysFowler'']', '[''纪录片'']', ' www.bbc.co.uk/programmes/b00s1lc8', '[''英国'']', '', '[''英语'']', '', '[''美味的花园'']', 'https://img3.doubanio.com/lpic/s4583781.jpg', '《食材花园》以阿莉丝·福勒自己的亲身经历为创作素材，分三集介绍了常见水果蔬菜花卉的种植、保存方法和日本草本印染。跟随阿莉丝·福勒的脚步，你可以看见在她的花园里可以种植出什么样的水果和蔬菜，甚至可食花卉，然后跟她一起享受收获后的美味菜肴。本片节奏清新明快，穿插童真可爱的简笔画，为当下的都市人营造出“久在樊笼里，复得返自然”的心境，也编织出一个纯朴美好的田园生活。阿莉丝·福勒是个园艺家兼作家，从小在乡下长大。她的理念“自己动手种植蔬菜、养鸡，尽量不去买商店里的蔬果，而改吃自家种的”正在被越来越多的年轻人所追捧喜爱。', '[]', '[''20272250'', ''11528393'', ''6719039'', ''6073643'', ''26313742'', ''3689938'', ''6785083'', ''11532593'', ''6866813'', ''25856416'']');
