INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10467766', 'O-PARTS～オーパーツ～', '5.9', '2012', '[''今井和久'', ''植田尚'']', '[''黒岩勉'']', '[''丸山隆平'', ''忽那汐里'', ''伊藤步'', ''染谷将太'', ''高梨临'', ''川岛润哉'']', '[''科幻'']', ' http://www.fujitv.co.jp/O-PARTS/index.html', '[''日本'']', '', '[''日语'']', '', '', 'https://img5.doubanio.com/view/movie_poster_cover/lpst/public/p2135722226.jpg', '2141年、タイムマシン開発競争は日本の勝利に終わる。不可能と言われていた時空を超えることができる装置の開発に成功したのだ。ただしその装置が繋がることができるのは「過去」のみ。しかも、送ることができるのは「データ」だけ…。つまり文字情報をデジタル信号に変え、過去のコンピューターに送ることが可能になったのである。しかし、非合法組織が、その技術を進化させ「物質移動装置」を完成させてしまう。彼らは装置を利用して「タイムテロ」を計画。そして2012年それを実行に移す。過去に５人のテロリストを送り込み、未来の要人たちに対し「祖先を殺す」と脅し始めたのだ。一方、未来の日本政府は、実際に過去に人間を送り込む術がなかった。そこで、極秘裏に、過去の日本政府と電子メールでコンタクトを取る。「タイムテロ」を狙う未来からの侵入者を排除して欲しいと依頼したのだ。...(展开全部)', '[]', '[''6963187'', ''6872212'', ''3257982'', ''4300578'', ''3063780'', ''3197054'', ''10777691'', ''11589829'', ''6952645'', ''10800170'']');
