INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10484053', '俄罗斯之魅：苏联时代的俄国动画 Magia Russica: Russian Animation in Soviet Times', '', '2004', '[''MashaZurGlozman'', ''YonathanZur'']', '', '[''GarriBardin'', ''费多尔·希特鲁克'', ''尤里·诺尔施泰因'', ''IosifBoyarskiy'', ''EduardNazarov'', ''AleksandrTatarskiy'', ''MashaZurGlozman'']', '[''动画'', ''纪录片'']', ' www.ymfilms.co.il/139141/Magia-Russica-films', '[''俄罗斯'', ''以色列'']', '[''2004'']', '[''英语'', ''俄语'']', '96', '[''MagiaRussica'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1658118353.jpg', '1935年，迪斯尼动画影片在莫斯科放映，一名艺术学院学生费多尔·赫特鲁克（FyodorKhytruk）受到极大震撼，决心投身俄国动画创作。在苏联体制下，动画电影厂拥有国家全额投资，而且相比其他视觉艺术门类，受到的上层干扰最少，故此人才济济，创作活跃，涌现出众多大师级艺术家。苏联解体后，俄罗斯动画面临市场压力，处于艰难调整阶段。以色列纪录片《俄罗斯之魅》在业界翘楚费多尔·赫特鲁克指引下，造访多位俄国动画大师的工作室，拍摄他们独特的动画制作过程和生活方式，并对苏联动画名作进行解读，其中许多从未在西方放映。', '', '[]');
