INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10484596', '僵尸本拉登 Osombie', '3.3', '2012', '[''约翰·莱德库尔特·黑尔'']', '', '[''克里约翰塞维尔'', ''莫罗，贾森韦德'', ''丹妮尔Chuchran'', ''威廉·鲁比奥'', ''马修·里斯'']', '[''动作'', ''惊悚'', ''恐怖'']', '', '[''美国'']', '', '[''英语'']', '', '[''Ozombie'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1709273211.jpg', '美国一家制片公司决定在电影中将已被美军击毙的本·拉丹“复活”。这位“基地”组织前领导人将率领一支由僵尸恐怖分子组成的军团，对北约军队发动战争。据这部“奇思妙想”电影的制片人格里芬称，该电影剧本早在本·拉丹去年5月被击毙前就已经写就。而既然本·拉丹现在已经死亡，这部电影无疑将因此博得更多的噱头。影片中将有大量的沙漠战斗场景，同时还会包括浪漫爱情的元素。影片主打口号是，“本·拉丹将再死一次。”格里芬称，这部电影在犹他州拍摄，整个拍摄过程充满乐趣。他说，“我们使用了许多好莱坞的特效技术，影片中有大量的动作场面。”他还表示，到目前为止，该影片的受期待情况相当乐观。', '', '[''2083834'', ''10583909'', ''6781974'', ''25823928'', ''3568456'', ''1974102'', ''21776365'', ''10606890'', ''7054757'', ''6513912'']');
