INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10487256', '一生只爱你', '5.5', '2012', '[''周德华'']', '[''宋毓建'']', '[''傅淼'', ''于小伟'', ''毕彦君'', ''姚安濂'', ''阎学晶'', ''林家川'', ''丁嘉丽'', ''董维嘉'', ''陈小纭'']', '[''爱情'']', '', '[''中国大陆'']', '', '[''汉语普通话'']', '', '', 'https://img3.doubanio.com/lpic/s7664842.jpg', '上世纪席卷全国的十年浩劫，造就了人世间几多悲欢离合与爱恨情仇。位于河北省定县的东风文工团，18岁的女孩石小青（傅淼饰）和19岁的北京知青刘胜利（于小伟饰）相恋，二人被部队的宣传队选中，即将携手成为光荣的文艺兵。可是就在火车即将开动的一刻，一场变故却造成了二人长达数十年的别离。文工团领导出面阻挠，拦下了小青，一对恋人从此生离死别。小青如同一朵泥沼中的莲花，纯洁靓丽却遭到恶徒的觊觎。道貌岸然的秘书李志高占有小青，使她患有身孕，产下女婴。在那样的年代，她背负的耻辱可想而知。在之后的岁月里，小青与胜利各自经历着人生的种种起伏、荣耀与磨难，他们一次次擦肩而过，心中对爱情的笃定却从未改变……', '[]', '[''20391199'', ''4821037'', ''6041203'', ''6057814'', ''3027101'', ''25869887'', ''11500286'', ''21348967'', ''25827882'', ''10563794'']');
