INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10514822', '罪与罚 罪と罰 A Falsified Romance', '7.2', '2012', '[''麻生学'']', '[''神山由美子'', ''藤本匡介'']', '[''高良健吾'', ''水川麻美'', ''伊藤步'', ''堀部圭亮'', ''中尾明庆'', ''桥本爱'', ''染谷将太'', ''朝仓亚纪'', ''万田久子'', ''田中哲司'', ''伊武雅刀'']', '', ' http://www.wowow.co.jp/dramaw/tsumitobatsu/', '[''日本'']', '', '[''日语'']', '', '', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2160827100.jpg', '现代化的繁华都市，人与人之间的关系变得充满铜臭气息，心和心的距离却相隔几万光年。考取大学失败的裁弥勒（高良健吾饰）将自己封闭在公寓中，极端的自尊和自卑形成鲜明对比，共同冲击着他那颗纤弱敏感的心。谁人能够想到，此时的他竟然悄悄制定这一个恐怖的计划。他憎恶那些伤害他人和自己的害虫，发誓要将害虫一扫而光。为此，裁将目标锁定在组织援交的高中女生马场光（桥本爱饰）的身上。他以全能的审判者的身份出现，却无法逃避心底的折磨与灵魂的拷问……本片根据俄罗斯作家陀思妥耶夫斯基的经典名作改编的漫画拍摄而成。', '[]', '[''10458659'', ''25826603'', ''10540063'', ''6805868'', ''3546062'', ''25783407'', ''4962967'', ''13935372'', ''6724680'', ''25880994'']');
