INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10514844', '相爱十年', '7.8', '2014', '[''刘惠宁'']', '[''霍昕'']', '[''邓超'', ''董洁'', ''高虎'', ''王大治'', ''肖雨雨'', ''廖学秋'', ''杨新鸣'']', '[''爱情'']', '', '[''中国大陆'']', '', '[''汉语普通话'']', '', '[''天堂向左，深圳向右'', ''深圳爱情故事'', ''相爱10年'', ''TenYearsofLove'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2002199353.jpg', '20世纪90年代，还是一片纯然率真的肖然（邓超饰）、刘元（高虎饰）、陈启明（王大治饰）与韩灵（董洁饰）即将告别他们的大学时代，走上正酝酿着巨大变革的社会。那时的韩灵就如同一个遗世独立的仙子，让男孩们魂牵梦绕。穷学生出身的肖然得到了女孩的芳心，而除了爱情外，往后的人生还有太多太多需要考虑的现实问题。受时代浪潮和命运际遇的推动左右，好哥们儿们先后来到了改革开放的前沿深圳，这里充满了各种机遇，同时也有未知。成功、堕落、友情、背叛、爱情、金钱。在光怪陆离的沿海城市，理想与物欲激烈碰撞，年轻人们用十年的时间考量着各自的心与灵魂……本片改编自慕容雪村的小说《天堂向左深圳向右》。', '[]', '[''5377708'', ''2157130'', ''2300194'', ''5958582'', ''25840705'', ''2248550'', ''6129693'', ''4735188'', ''2583174'', ''25711530'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10514846', '金太狼的幸福生活', '6.9', '2012', '[''余淳'']', '[''娟子'']', '[''宋丹丹'', ''李小璐'', ''范明'', ''王雷'', ''杜源'', ''林丛'', ''王博谷'', ''巴图'', ''秦卫东'', ''李勤勤'', ''雷佳'', ''莫岐'', ''谭松韵'', ''周冬齐'', ''濮存昕'', ''邵峰'']', '[''爱情'', ''家庭'']', '', '[''中国大陆'']', '', '[''汉语普通话'']', '', '', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1556400954.jpg', '北京某娱乐报社记者小米（李小璐饰）与妇产科医生金亮（王雷饰）地下恋多年，偷偷领了结婚证。此事在小米家引起了轩然大波。小米父母早年离婚，母亲王淑华（宋丹丹饰）寡居，而父亲米范（范明饰）已经续弦。本来，王淑华希望小米找个北京男，谁想冒出个北漂男，而且还生命煮成熟饭。这令她大为恼火，大动干戈，心脏病都犯了，在前夫的劝说下，好歹接受了这个现实。不过，办婚礼的地点又却引发了双方新的战争。来自东北煤矿金亮爸为他们购置了新房，并进行了装修。但是希望婚礼在沈阳举行。这遭到了王淑华的坚决反对，她坚持婚礼要在北京办。后来，小两口为了都不得罪，打算改旅行结婚。结果，王淑华坚持要陪小两口旅行结婚当电灯泡……双方互不相让，你来我往，各显神通。令金亮与小米夹在中间，左右为难。他们的爱情经受得住这此起彼伏的考验吗？', '[]', '[''10527278'', ''5395243'', ''6894265'', ''10726961'', ''10833920'', ''6967950'', ''6393340'', ''6796113'', ''26266571'', ''6788274'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10514849', '新编辑部故事', '4.6', '2013', '[''郑晓龙'', ''王建军'']', '[''巩向东'']', '[''吕丽萍'', ''黄海波'', ''井柏然'', ''陈好'', ''王千源'', ''焦俊艳'']', '[''喜剧'']', '', '[''中国大陆'']', '', '[''汉语普通话'']', '', '[''新编辑部的故事'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2021176143.jpg', '转眼二十年岁月，历经风雨的《人间指南》杂志更新换代，同时面临着生死存亡的重要时刻。我们所熟悉的老一辈编辑们，或者退休，或者作古，或者下海经商，或者在娱乐圈开辟新天地，而今只有戈玲（吕丽萍饰）支撑着倾注了一生感情的编辑部。当她带领油嘴滑舌的摄影师袁帅（黄海波饰）、刘书友的儿子刘向前（王千源饰）、帅气的网遍何澈澈（井柏然饰）、月光一族娱乐编辑欧小米（焦俊艳饰）艰难前行之际，满嘴英文的运营总监安妮（陈好饰）空降编辑部，并大刀阔斧对杂志进行改革，曾经的《人间指南》改换门庭变成了追逐时尚和热点的《WWW》。在这一过程中，新老两代出版人的碰撞在所难免，而各种有趣的小故事也层出不穷……', '[]', '[]');
