INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10521688', '美好2012', '6.4', '2012', '[''顾长卫'', ''蔡明亮'', ''金泰勇'', ''许鞍华'']', '', '[''阎连科'', ''吴镇宇'', ''孔晓振'', ''李康生'']', '[''剧情'']', '', '[''香港'']', '[''2012-03-24(香港)'']', '[''粤语'', ''汉语普通话'', ''韩语'']', '81', '[''美好2012之大师微电影'', ''Beautiful2012'']', 'https://img1.doubanio.com/lpic/s9090617.jpg', '影片由4部短片组成，在四个组成部分中，顾长卫的《龙头》讲述了一个作家、女孩、艺术家和拾荒者，在年年月月分分秒秒的等待中，或聚或散的故事。而蔡明亮则与老搭档李康生合作，讲述了叫“小康”的《行者》在城市里奔走的故事。金泰勇的《你何止魅力》是个喜剧，男主角为了安抚病重的老父，花钱雇佣假女友，没想到假戏真做。而许鞍华则继续聚焦香港小市民的生活，吴镇宇在《我的路》里是一个深得女人心的好老公和老爸爸，但是，他内心的向往却是——变性！', '', '[''10583899'', ''10583898'', ''6101729'', ''10583900'', ''4063356'', ''3562861'', ''3836276'', ''26325366'', ''2253732'', ''25726631'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10521701', '三生三世 聂华苓 三生三世 聶華苓', '8.6', '2012', '[''陈安琪'']', '[''陈安琪'']', '[''聂华苓'', ''白先勇'', ''莫言'', ''钟玲'', ''余华'', ''蔣勳'']', '[''纪录片'', ''传记'', ''历史'']', '', '[''香港'']', '[''2013-01-12(香港)'']', '[''汉语普通话'']', '98', '[''OneTreeThreeLives'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1944740331.jpg', '人稱「世界文學組織之母」的聶華苓是20世紀華人文學界最重要的推手，白先勇、林懷民、蔣勳，以及諾貝爾文學獎得主莫言，香港作家李怡、董啟章、鍾玲、潘耀明都曾是她家客廳的座上客。現年八十七歲的聶華苓生於中國武漢，1949年與家人逃到台灣，後因編輯【自由中國】受白色恐怖牽連，之後赴美與丈夫PaulEngle創辦影響力龐大的愛荷華大學「國際寫作計畫」。通過他們的「國際寫作計劃」，世界各地作家越過意識形態聚集一堂。1979年，大陸、台灣和香港的作家隔絕三十載，首次相見於愛荷華。聶華苓曾自喻為「一棵樹，根在大陸，幹在臺灣，枝葉在愛荷華。」香港導演陳安琪花三年時間、往來兩岸三地與美國，拍攝紀錄片《三生三世聶華苓》。聶華苓的一生，恍如一段三生三世的飄泊旅行，交織出了當代海外傑出華人作家的清晰身影與美麗，也刻劃了她和詩人伴侶保羅．安格爾至死不渝的堅定情感。', '[''第4届豆瓣电影鑫像奖'', ''鑫豆单元 最佳纪录长片(提名)'']', '[''21359627'', ''5969389'', ''4074216'', ''10456755'', ''10521700'', ''1370243'', ''20415233'', ''6861624'', ''4154541'', ''26284538'']');
