INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10527275', '来自新世界 新世界より', '8.8', '2012', '[''石浜真史'']', '[''中瀬理香'', ''十川诚志'']', '[''種田梨沙'', ''東條加那子'', ''梶裕贵'', ''花泽香菜'', ''工藤晴香'', ''高城元气'', ''藤堂真衣'', ''村瀬歩'', ''东地宏树'', ''伊藤美纪'', ''榊原良子'', ''星野贵纪'', ''浪川大辅'', ''平田广明'', ''堀江由衣'', ''杉田智和'']', '[''剧情'', ''动画'']', ' www.tv-asahi.co.jp/shinsekaiyori/', '[''日本'']', '', '[''日语'']', '', '[''自新世界'', ''FromtheNewWorld'']', 'https://img1.doubanio.com/view/movie_poster_cover/lpst/public/p1438419268.jpg', '《来自新世界》改编自贵志祐介所著的科幻类小说作品。1000年后的日本，孩子们不断地消失，只存在想象中的恐怖动物与人类展开殊死战争。反乌托邦式的未来超级社会“新世界”，是口吐真言凭藉“咒力”就能移动物体的人类，与有著等同于普通人智力的生物“妖鼠”共存的世界。乍看之下一切和平，但“新世界”的孩子们的行动被彻底地控制和管束著，不合适的记忆被消去，被认为有问题的孩子，如同不良产品般被分开处理。得到被称为“咒力”的特殊能力的人们在“八丁标”的结界中促成一个小小的团体，无论是思想还是对外界的认知，记忆、爱、甚至生命的支配都被统治着。在这其中，渡辺早季、朝比奈覚、秋月真理亜、伊东守、青沼瞬五位少年和少女们，为了守护日益衰弱的世界和同伴，投身到了各种各样的冒险的旅程中......', '[]', '[''10569144'', ''5349275'', ''6085356'', ''5416070'', ''3326764'', ''5985869'', ''22790508'', ''3893384'', ''19967854'', ''20495792'']');
