INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10538001', 'Back to the Start', '', '2011', '[''ChipotleMexicanGrill'']', '[''ChipotleMexicanGrill'']', '', '[''动画'', ''短片'']', ' http://vimeo.com/28349065', '[''美国'']', '[''2011年9月7日'']', '[''无对白'']', '2', '', 'https://img1.doubanio.com/lpic/s8934159.jpg', '谁都渴望过上舒适美满的生活，让自己的妻儿无忧无虑。为了美好的未来，人们不断努力，却往往在这一过程中迷失了方向。空气清新的郊外，农夫新添了一个小宝宝。他养猪卖钱，猪越来越多，于是乎又盖起了农舍、谷仓。接着养了许多的奶牛。农庄越改越大，现代化交通工具和机械也引入进来。全自动的农场拔地而起，瘦瘦的小猪们吃进去化学品混合饲料，迅速长为大肥猪。肥猪送入食品加工厂，经切割加工后运往各地销售。一切都是那么高效率，一切看似那么美好。可总有什么地方不对劲，为了美好的未来，我们是否丢失了一些最本质的东西？', '', '[]');
