INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10549565', '国家地理频道：谢赫•扎耶德清真寺 National Geographic: The Sheikh Zayed Grand Mosque', '', '2009', '', '', '', '', '', '[''美国'']', '', '[''英语'']', '47', '', 'https://img3.doubanio.com/f/shire/21e771816d14ede9a77a01b6c7874c4b05dd05e2/pics/movie-default-large.gif', '扎耶德清真寺是阿联酋最大的清真寺，世界第六大清寺。整个建筑群都用来自希腊的汉白玉包裹着，非常的庄严肃穆，而那些精美的雕刻则是来自中国工匠的手艺。清真寺中还有一块世界第一大手工编制地毯，由伊朗马什哈德地区的1,200名妇女手工编制而成。整块地毯面积达5,627平方米，用的都是来自伊朗和新西兰的顶级羊绒。另外，有一面雕刻了古兰经经文的墙壁，整卷圣典的每一个文字都由24K金镀成，非常奢华。这个礼拜堂可容纳超过6,000位朝圣者，整个扎耶德清真寺可容纳超过40,000名朝圣者同时进行礼拜。此外，扎耶德清真寺也是是唯一一座允许女性从正门进入的清真寺，在男女等级悬殊的阿拉伯地区，这样的意义更是非比寻常。', '', '[]');
