INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10566646', '正义联盟 第二季 Justice League Season 2', '9.1', '2003', '[''伽德纳·福克斯'']', '', '[''DenisKacenga'', ''卡尔·卢波利'', ''乔治·纽伯恩'']', '[''动画'']', '', '[''美国'']', '', '[''英语'']', '', '[''正义联盟'']', 'https://img1.doubanio.com/lpic/s8993727.jpg', '超人、蝙蝠侠、神力女超人、闪电侠、鹰女、火星猎人和绿幻侠一共7名超级英雄组成有史以来地球上最为强大的团队，共同对抗外星人的入侵，一起保卫地球消灭来犯的外星人，巫师，超级恶棍以及各种可能出现的威胁！本片由DCComic推出，经由时代华纳出品，在欧美动画市场上有着巨大的影响。其中超人和蝙蝠侠的画风和我国曾引进的版本基本相同，正像前面提到过的有的时候工作太巨大，以至于对于一个英雄来说太过艰难，所以要由各个英雄来合作解决，于是华纳旗下的众多英雄携起手来组成JusticeLeague来对抗各方邪恶势力对和平的挑战。虽然在影片里各个英雄的能力都有所表现，不过还是感到大家都变弱了，或许这样倒是更真实些吧。', '[]', '[''25933471'', ''3220631'', ''5153018'', ''3688942'', ''6425150'', ''26356561'', ''5324766'', ''5122641'', ''22622973'', ''26424968'']');
