INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10567562', '韩战全彩实录 Korea: The Forgotten War in Colour', '7.8', '2010', '[''StewartBinns'']', '[''StewartBinns'']', '', '[''纪录片'']', '', '[''英國UK'']', '[''2010'']', '[''英语'']', '45', '', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2325619671.jpg', '2010年6月23日是韩战爆发50周年纪念日，这场血腥冲突当年差点让全世界陷入世纪核子大战，同时也重创了一个国家，这场战争的伤痕之深，使得南北韩至今仍处于分裂对立的态势。《韩战全彩实录》将以前所未见的观点诉说韩战的故事，透过韩国民众的眼光，带领观众重新认识这场战争。"全彩实录"制作人史都华宾恩斯与雅得安伍德再度携手合作，透过鲜活生动的彩色影像，扣人心弦与充满戏剧张力的配乐，制作《韩战全彩实录》，以全新角度介绍韩战的故事。韩战全彩实录引用韩国人民的第一手讲述，并将当年的原始彩色底片扫描转为HD高画质影片，其中有许多画面都是最近才发现，之前从未在电视上播出。本节目将以独特角度深入探讨东西方的冲突，共产主义挑战自由民主的真实对峙，见识韩国人民如何经历这场血腥的内战。上集：分裂的国家，冲突一触即发的世界1950年夏天韩国人民被卷入才刚开始的全球冷...(展开全部)', '', '[''26278478'', ''25780590'', ''3629230'', ''25868704'', ''20513939'', ''3724118'', ''20427004'', ''1966355'', ''26383939'', ''10741871'']');
