INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10569020', '无言', '5.1', '2012', '[''钟德胜'']', '[''钟德胜'', ''吕聿来'']', '[''Pierre-MathieuVitali'', ''俞融蓉'', ''高崎纶'']', '[''同性'']', '', '[''香港'']', '[''2012-01-15（香港）'']', '[''汉语普通话'']', '92', '[''Speechless'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1609760505.jpg', '“无言”的故事发生在中国南方的边城。年轻的法国交换生路克（Pierre-MathieuVitali饰）在偏僻的河岸被发现时全身赤裸，醒来后处于失语和失忆状态。他对医院的男看护小江（高崎纶饰）感到莫名的亲切。当地的派出所准备把路克送往大城市精神病院强制治疗，但小江将路克半途拐走，并安置在舅舅家里暂住。尽管路克想不起发生了什么，但小江误打误撞的发现，原来路克的无言和一个叫宁（俞融蓉饰）的女子有关。而当全部的记忆还魂，带来的真相却是生命不能承受之重......', '', '[''10531557'', ''2080935'', ''6725030'', ''10458904'', ''2287307'', ''3772633'', ''24869398'', ''10945675'', ''24705692'', ''3864850'']');
