INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10569133', '半夜不要照镜子', '3.5', '2012', '[''牛朝阳'']', '[''牛朝阳'']', '[''关智斌'', ''李曼筠'', ''徐汶萱'', ''张钧涵'', ''及莉'', ''姜梦茹'', ''刘静怡'', ''苏悦华'', ''乌雅心颜'']', '[''悬疑'', ''惊悚'']', '', '[''中国大陆'']', '[''2012-06-14(中国大陆)'']', '[''汉语普通话'']', '88', '[''Whointhemirror'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1568062440.jpg', '正是军阀混战时期，飞烟（李曼筠饰）、江桓（关智斌饰）、陈少锦（张钧涵饰）、含玉（及莉饰）、野妹（徐汶萱饰）等五个青年男女辗转来到荒凉破败、阴森寥落的巫镇。城中死气沉沉，全无半点生机。这一行人来到目的地白家酒楼，并在此下榻。破破烂烂的酒楼早已人去楼空，店内挂着一幅红盖头新娘的画，围绕画中的新娘曾经发生过悲惨而恐怖的故事。据说画出现的地方就会出现镜鬼，同时也意味着会有人死亡。深更半夜，青年男女无心睡眠，他们不知不觉中打破了半夜不要照镜子的禁忌，死亡尾随而至，他们将经历最为恐怖的一段时光……', '', '[''10748193'', ''6013868'', ''25742254'', ''11511025'', ''6753096'', ''21264290'', ''10478675'', ''19962668'', ''24877008'', ''10543682'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10569143', '机器人笔记 ROBOTICS;NOTES', '7.4', '2012', '[''野村和也'']', '[''花田十辉'']', '[''木村良平'', ''南条爱乃'', ''细谷佳正'', ''名塚佳织'', ''徳井青空'', ''钉宫理惠'']', '[''动画'']', ' http://roboticsnotes.tv', '[''日本'']', '', '[''日语'']', '', '[''铁甲梦工场(港)'', ''ロボティクス・ノーツ'']', 'https://img1.doubanio.com/view/movie_poster_cover/lpst/public/p1971339918.jpg', '2019年，现代科技高度发达，机器人普及到人类世界的方方面面。中央种子岛高中机器人部，是一个只有两个人的小团体，痴迷于掌上游戏的八汐海翔（木村良平饰）和超级喜欢机器人的部长濑乃宫秋穗（南条爱乃饰），面临因人数过少而废部的局面。小秋梦想带着本部最大的梦想——等高大小的机器人GUNVARL参加来年举行的东京市博会。为了顺利征得经费，机器人部必须在ROBO-ONE大赛上取得优胜。峰回路转、紧张刺激的比赛后，机器人部顺利存活，还吸纳了在机械方面拥有极高天赋的日高昴（细谷佳正饰）入部。巨型机器人的建造有条不紊地进行着，瑰丽的梦想恣意绽放，而阴谋的黑影也悄悄笼罩种子岛，这一切全与九年前的事件息息相关……本片根据志仓千代丸的原作改编。', '[]', '[''20438510'', ''19967854'', ''5985869'', ''6091376'', ''6852358'', ''10833945'', ''6796122'', ''5413681'', ''10864705'', ''6085356'']');
