INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10574471', '我的路', '7.4', '2012', '[''许鞍华'']', '[''吕筱华'']', '[''吴镇宇'', ''梁琤'', ''陈丽云'']', '[''剧情'', ''短片'']', ' v.youku.com/v_show/id_XMzc5Njc4NTg4.html', '[''香港'']', '[''2012-03-22'']', '[''粤语'']', '20', '[''Myway'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2271757270.jpg', '熙熙攘攘的弹丸之地香港，有那么一个群体，其生存的空间更显逼仄，周国才（吴镇宇饰），男，某银行职员，人过中年的他衣食无忧，妻子（梁琤饰）美丽，儿子英俊，但是他为人新鲜的外表下，却有着难以道出的苦衷。周一直向往成为女人，总是婚姻生活也没让他过回所谓“正常”的生活，他时常背着妻子买来漂亮的裙子，然后打扮入时行走在大街小巷，体会片刻的自在。直到一切大白天下，他的人生彻底改变，婚姻也最终破裂。如今的周以女人的装束出入公众场合，周围的人虽然礼貌地保持缄默，可是从他们复杂的眼神中还是可以感受到质疑、鄙夷与不解。即便如此，周还是等待着他的手术，等待着一名女子的诞生……本片为优酷《美好2012》微电影系列中的一部。', '[''第3届豆瓣电影鑫像奖'', ''鑫豆单元 最佳短片(提名)'']', '[''10521688'', ''10583899'', ''6311988'', ''10583900'', ''6888711'', ''25726631'', ''3798935'', ''1291563'', ''1449825'', ''6101729'']');
