INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10583103', '纯种狗的悲哀 Pedigree Dogs Exposed', '9.0', '2008', '', '', '', '', '', '[''英国'']', '[''2008-08-19(英国)'']', '[''英语'']', '59', '[''纯种狗曝光'']', 'https://img1.doubanio.com/lpic/s9025769.jpg', 'BBC播出的纪录片《纯种狗的悲哀》着重提出了很多纯种犬只所承受的严重健康问题。人类为了犬类比赛以及所谓的“优良血统”而数十年将纯种狗近亲繁殖，如今导致了诸多犬种从癫痫症到癌症的一系列威胁生命的病症。查尔斯国王骑士獚犬因为头骨太小而不能容纳大脑；牛头犬的怪异形状致使他们无法自然交配生产；金毛犬极易患上癌症……人类手下的生命，不是产品，并不存在标准化和优劣，生而平等，他们都有活下去的权利。', '', '[''4783311'', ''10743960'', ''24752793'', ''24695878'', ''5374378'', ''11606340'', ''3987473'', ''24752524'', ''5370346'', ''10545998'']');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10583637', '白云山传奇', '2.9', '2012', '[''张鑫'', ''曹利民'']', '[''许金华'']', '[''袁志博'', ''关长珠'']', '[''剧情'', ''动作'', ''古装'']', '', '[''中国大陆'']', '', '[''汉语普通话'']', '91', '[''ThelegendinBaiyunMountain'']', 'https://img3.doubanio.com/lpic/s9026224.jpg', '民国初年，四个江洋大盗劫得白云山道观的镇观之宝—乌龙壁。为了分赃，他们来到乌龙镇找赌王毛开源主持一场赌局，胜者得玉。消息传出，刀客莫俊风、神偷崔妙手、恶客燕子飞等，各路豪杰纷纷来到乌龙镇，他们都企图伺机捞到好处。侦缉队长也率女儿小红、助手彭双双为了缉拿盗贼也悄然到来。经过一场善与恶的大博杀之后，真相大白，原来是护宝的镖头屠一坤，利用四大盗自相残杀，监守自盗窃得乌龙壁。当屠一坤洋洋得意之时，正义的铁匠、刀客莫俊风与他进行了一场生死较量，神偷也在暗处机智地助战，将屠一坤制服。乌龙壁最终完璧归赵，由小红和莫俊风送回了白云山。而彭双双却为此献出了年青的生命。老道长说：“上善若水，白云山永远怀念为护宝奉献热血的英雄。”', '', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10583840', '老哨卡', '7.4', '2012', '[''杜粮'', ''德格娜'']', '[''郑宪民'', ''张建华'']', '[''亓航'', ''鲍里斯·佩德鲁索夫'', ''王政'', ''任山'', ''娅茹'', ''菲德尔·赛尔金'', ''斯维特兰娜·茨威琴科'']', '[''剧情'', ''战争'']', '', '[''中国大陆'']', '[''2014-07-09(成都地区)'']', '[''汉语普通话'', ''俄语'']', '98', '[''高高的瞭望塔'', ''北纬52度'', ''Latitude52'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p2190901411.jpg', '影片从描述一个小战士阿尔斯楞的成长经历折射出当年中苏两国虽然交恶，但两国人民之间的友谊却从没有中断过。忠于祖国，保卫祖国是士兵的天职；但士兵都是人，人的本性告诉我们在遇到来自共同的威胁时，鼎力相助是义不容辞天经地义的。故事着重强调了阿尔斯楞的个人壮举超出了任何界限，从而体现了小人物身上所散发出的人性的永恒光辉；通过他的事迹教育了官民，同时更弘扬了爱国主义和国际人道主义精神。彰显了和平是人类共同追求的美好理想，维护和平避免战争才是我方加强军事实力的真正目的。', '[''第29届中国电影金鸡奖'', ''最佳中小成本故事片(提名)'']', '[]');
INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10583843', '极限冲刺', '5.7', '2010', '[''杨虎'']', '[''温豪杰'', ''张东'', ''宫尚娟'']', '[''王挺'']', '[''剧情'', ''纪录片'']', '', '[''中国大陆'']', '[''2011-08-01'']', '[''汉语普通话'']', '90', '[''Extreme'']', 'https://img1.doubanio.com/view/movie_poster_cover/lpst/public/p2272214199.jpg', '《极限冲刺》是一部反映总参军事五项队事迹的纪实故事片。据介绍，电影《极限冲刺》以2009年中国军事五项队赴德国比赛为主线，用纪实的手法，跟踪拍摄了运动队集训、队员选拔、日常训练、出国前的每一次考核、以及在德国的比赛实况等。另外，影片还真实记录了军事五项队如何在不利条件下奋力拼搏，男女队双双夺冠的全过程。首次揭秘了鲜为人知的军事五项队队员生活和训练细节，以及队员们真实感人的成长经历和心路历程。', '', '[]');
