INSERT INTO mirs_movie (douban_id, name, douban_rating, release_year, directors, screenwriters, actors, types, official_website, origin_place, release_date, languages, runtime, another_names, cover_link, synopsis, awards, also_like_movies) VALUES ('10583898', '她的演技 그녀의 연기', '6.1', '2012', '[''金泰勇'']', '[''金泰勇'', ''金英贤'']', '[''孔晓振'', ''朴熙顺'']', '[''短片'']', '', '[''中国大陆'', ''韩国'']', '[''2012-06-13(韩国)'']', '[''韩语'']', '27', '[''你何止美丽'', ''YouAreMoreThanBeautiful'']', 'https://img3.doubanio.com/view/movie_poster_cover/lpst/public/p1990129201.jpg', '在一个略有阴霾的日子里，成人片女演员英喜（孔孝真饰）来到济州岛，和他见面的是当地一个名叫哲秀（朴熙顺饰）的内向木讷男子。哲秀雇佣英喜饰演他的结婚女友，以此来敷衍一直催哲秀结婚的爸爸——如今已是躺在医院随时便会离开人世的垂垂老者。起初毫不知情的英喜快乐地调侃哲秀，享受济州岛的自然风光，甚至自顾自编出与哲秀的恋爱经过。可是在看到哲秀父亲的情况时，她的心中却涌起别样情愫。在这间冷冰冰的病房里，英喜唱起了高亢悲凉的盘索里……本片为优酷《美好2012》微电影系列中的一部。', '', '[''10521688'', ''10583899'', ''26353322'', ''10583900'', ''4063356'', ''6902340'', ''3563217'', ''5377122'', ''11599330'', ''2046915'']');
